import 'package:flutter/material.dart';import 'package:provider/provider.dart';import '../providers/caunter.dart';import '/pages/second_page.dart';/// FirstPage ekranıclass FirstPage extends StatefulWidget {  /// FirstPage ekranı  const FirstPage({super.key});  @override  State<FirstPage> createState() => _FirstPageState();}class _FirstPageState extends State<FirstPage>{  @override  Widget build(BuildContext context) {    print('FirstPage rebuild');    return Scaffold(      body: Column(        mainAxisAlignment: MainAxisAlignment.center,        children: [           const Text('Sayfa 1',            style: TextStyle(fontSize: 24, fontWeight: FontWeight.bold),          ),          Builder(            builder: (context) {              final caunt = context.read<Caunter>().count;              return  Text('$caunt',                style: TextStyle(fontSize: 24, fontWeight: FontWeight.bold),              );            }          ),          Center(            child: ElevatedButton(              onPressed:  (){                context.read<Caunter>().increment();                setState(() {                });              },              child: const Text('Arttır'),            ),          ),          Center(            child: ElevatedButton(              onPressed:context.read<Caunter>().decrement,              child: const Text('Azalt'),            ),          ),          Center(            child: ElevatedButton(              onPressed: () {               Navigator.push(context, MaterialPageRoute(builder: (_) => const SecontPage()));              },              child: const Text('İkinci Sayfaya Git'),            ),          ),        ],      ),    );  }}