import 'package:flutter/material.dart';import 'package:provider/provider.dart';import 'package:provider_egitim/providers/caunter.dart';/// SecontPage ekranıclass SecontPage extends StatefulWidget {  /// SecontPage ekranı  const SecontPage({super.key});  @override  State<SecontPage> createState() => _SecontPageState();}class _SecontPageState extends State<SecontPage>{  @override  Widget build(BuildContext context) {    return Scaffold(      body: Column(        mainAxisAlignment: MainAxisAlignment.center,        children: [          const Text('Sayfa 2',          style: TextStyle(fontSize: 24, fontWeight: FontWeight.bold),          ),          Builder(              builder: (context) {                final caunt = context.watch<Caunter>().count;                return  Text('$caunt',                  style: TextStyle(fontSize: 24, fontWeight: FontWeight.bold),                );              }          ),          Center(            child: ElevatedButton(              onPressed: () {                Navigator.pop(context);              },              child: const Text('Geri Dön'),            ),          ),        ],      ),    );  }}